// Traitement et pr√©paration article BoomLaLaBoom pour Supabase
const sourceInfo = $('üéØ S√©lecteur Article BoomLaLaBoom').first().json;
const inputData = $input.first();

let rawContent = '';
if (inputData?.json?.message?.content) {
  rawContent = inputData.json.message.content;
} else if (inputData?.json?.content) {
  rawContent = inputData.json.content;
} else if (typeof inputData?.json === 'string') {
  rawContent = inputData.json;
} else {
  throw new Error('Format de r√©ponse OpenAI non reconnu');
}

rawContent = rawContent.replace(/^```json\s*/i, '').replace(/```\s*$/i, '').trim();

let articleJson;
try {
  articleJson = JSON.parse(rawContent);
} catch (e) {
  const match = rawContent.match(/{[\s\S]*}/);
  if (match) {
    articleJson = JSON.parse(match[0]);
  } else {
    throw new Error('Format JSON invalide');
  }
}

const normalizeSlug = (text) => {
  return text
    .toLowerCase()
    .trim()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/[^a-z0-9\s-]/g, '')
    .replace(/\s+/g, '-')
    .replace(/-+/g, '-')
    .replace(/^-|-$/g, '');
};

const stripHtml = (html = '') => html.replace(/<[^>]+>/g, ' ');
const wordCount = (text = '') => text.trim().split(/\s+/).filter(Boolean).length;

const ensureSlug = (slug, title) => {
  const base = slug || title || `article-${Date.now()}`;
  return normalizeSlug(base);
};

const contentFr = articleJson.content_fr || '';
const words = wordCount(stripHtml(contentFr));

const finalArticle = {
  title_fr: articleJson.title_fr || sourceInfo.titre,
  slug_fr: ensureSlug(articleJson.slug_fr, articleJson.title_fr || sourceInfo.titre),
  excerpt_fr: articleJson.excerpt_fr || '',
  content_fr: articleJson.content_fr || '',
  meta_title_fr: articleJson.meta_title_fr || articleJson.title_fr || sourceInfo.titre,
  meta_description_fr: articleJson.meta_description_fr || articleJson.excerpt_fr || '',

  title_en: articleJson.title_en || '',
  slug_en: ensureSlug(articleJson.slug_en, articleJson.title_en || articleJson.title_fr || sourceInfo.titre),
  excerpt_en: articleJson.excerpt_en || '',
  content_en: articleJson.content_en || '',
  meta_title_en: articleJson.meta_title_en || articleJson.title_en || '',
  meta_description_en: articleJson.meta_description_en || articleJson.excerpt_en || '',

  title_es: articleJson.title_es || '',
  slug_es: ensureSlug(articleJson.slug_es, articleJson.title_es || articleJson.title_fr || sourceInfo.titre),
  excerpt_es: articleJson.excerpt_es || '',
  content_es: articleJson.content_es || '',
  meta_title_es: articleJson.meta_title_es || articleJson.title_es || '',
  meta_description_es: articleJson.meta_description_es || articleJson.excerpt_es || '',

  author_name: 'BoomLaLaBoom Team',
  read_time_minutes: Math.max(1, Math.ceil(words / 200)),
  thumbnail_url: sourceInfo.image_url || '',
  is_featured: !!sourceInfo.is_featured,
  published_at: new Date().toISOString(),
  view_count: 0,
  top_rank: null,
  row_number: sourceInfo.row_number,
  google_sheet_line: sourceInfo.ligne
};

return [{ json: finalArticle }];

